      byte b[] = java.security.MessageDigest.getInstance("MD5").digest( (user + ":" + realm + ":" + password ).getBytes());

      java.math.BigInteger bi = new java.math.BigInteger(1, b);

      String s = bi.toString(16);

      while (s.length() < 32)


        s = "0" + s;

      // String s is the encrypted password
